import React, {useState, useEffect} from "react";
import { Container, Button, Typography } from "@material-ui/core";
import TextInput from '../components/TextInput';
import EmailValidator from "email-validator";

function ForgotPassword() {
	const [email, changeEmail] = useState("");
	const [emailError, setEmailError] = useState("");
	const [emailChanged, setEmailChanged] = useState(false);

	const handleEmailChange = (event) => {
		setEmailChanged(true);
		changeEmail(event.target.value);
	}

	const mailErrorText = "Email cannot be empty!";

	const handleSubmit = (event) => {
		event.preventDefault();

		setEmailChanged(true);

		let errors = false;

		if(email.length === 0) {
			setEmailError(mailErrorText);
			errors = true;
		} else if(!EmailValidator.validate(email)) {
			setEmailError("Invalid email address!");
			errors = true;
		}

		if(!errors && emailError.length === 0) {
			alert("YES");
		}
	}

	useEffect(() => {
		if(email.length === 0) setEmailError(mailErrorText);
		else setEmailError("");

	}, [email]);

	return (
		<Container className="login-page">
			<div className="login-form">
				<Typography variant="h3" color="primary" className="login-head">Forgot Password</Typography><br />
				<form className="form">
					<TextInput
						error={emailChanged? (emailError.length === 0? false: true): false}
						helperText={emailChanged? (emailError.length === 0? null: emailError): null}
						id="email"
						label="Email"
						type="email"
						className="form-input"
						variant="outlined"
						value={email}
						onChange={handleEmailChange}></TextInput>
				</form>
				<Button className="login-btn" onClick={handleSubmit}>Send mail</Button>
			</div>
		</Container>
	)
}

export default ForgotPassword;
