import React, { useState } from 'react';
import './LoginPage.css';
import { Container, TextField, withStyles, Typography, Button } from "@material-ui/core";
import {Link} from "react-router-dom";

const Text = withStyles({
	root: {
		'& label': {
			color: 'rgba(255,255,255, 0.7)',
		},
		'& label.Mui-focused': {
			color: 'orange',
		},
		'& .MuiInput-underline:after': {
			borderBottomColor: 'orange',
		},
		'& .MuiInputBase-input': {
			color: 'black',
		},
		'& .MuiOutlinedInput-root': {
			'& fieldset': {
			  borderColor: 'rgba(255,255,255, 0.3)',
			  color: 'black',
			},
			'&:hover fieldset': {
			  borderColor: 'orange',
			},
			'&.Mui-focused fieldset': {
			  borderColor: 'orange',
			},
		  },
	}
})(TextField);

function LoginPage() {
	const [email, changeEmail] = useState("");

	const handleEmailChange = (event) => {
		changeEmail(event.target.value);
	}

	const handleSubmit = (event) => {
		event.preventDefault();
	}

	return (
		<Container className="login-page">
			<div className="login-form">
				<Typography variant="h3" color="primary" className="login-head">Login Now!</Typography><br />
				<form className="form">
					<Text
						id="email"
						label="Email"
						type="email"
						className="form-input"
						variant="outlined"
						onChange={handleEmailChange}></Text>
					<br />
					<Text
						id="password"
						type="password"
						label="Password"
						className="form-input"
						variant="outlined"></Text>
				</form>
				<Button className="login-btn" onClick={handleSubmit}>Login</Button>
				<Link className="link register-link">Don't have an account? Join the rebellion now!</Link>
			</div>
		</Container>
	)
}

export default LoginPage;
